    const [data, setData] = useState({
        palette: "1",
        name: "",
        job: "",
        phone: "",
        email: "",
        linkedin: "",
        github: "",
        photo: "",
    });
    /*   const [palette, setPalettes] = useState("1");
      const [name, setName] = useState("");
      const [job, setJob] = useState("");
      const [email, setEmail] = useState("");
      const [phone, setPhone] = useState("");
      const [linkedin, setLinkedin] = useState("");
      const [github, setGithub] = useState(""); */

    const handleInputLifting = (inputValue, inputName) => {
        //data[inputName] = inputValue; NO VALE

        setData({
            ...data,
            [inputName]: inputValue,
        });

        /*  if (inputName === "name") {
              setName(inputValue);
            } else if (inputName === "job") {
              setJob(inputValue);
            } else if (inputName === "palette") {
              setPalettes(inputValue);
            } else if (inputName === "email") {
              setEmail(inputValue);
            } else if (inputName === "phone") {
              setPhone(inputValue);
            } else if (inputName === "linkedin") {
              setLinkedin(inputValue);
            } else if (inputName === "github") {
              setGithub(inputValue);
            } */
    };

    const handleReset = () => {
        setData({
            palette: "1",
            name: "",
            job: "",
            phone: "",
            email: "",
            linkedin: "",
            github: "",
            photo: "",
        });
        /* setName("");
            setJob("");
            setPalettes("1");
            setEmail("");
            setPhone("");
            setLinkedin("");
            setGithub(""); */
    };

    return ( <
        main className = "mainProfile mainProfile__wrapper" > { " " } { /* <div className="mainProfile__wrapper"> */ } { " " } <
        PhotoCard data = { data }
        handleReset = { handleReset }
        />{" "} <
        Form data = { data }
        handleInputLifting = { handleInputLifting }
        />{" "} { /* </div> */ } { " " } <
        /main>
    );
}

export default Main;
